function Inventory:GetItemCount(item)
    found = 0
    if self.Inventory ~= nil then
        for k, v in pairs(self.Inventory) do
            if k == item then
                if #v <= 0 then
                    found = 0
                else
                    found = #v
                end
            end
        end
    end
    return found
end
